package com.utyf.pmetro.util;import android.content.Context;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.ArrayAdapter;import android.widget.TextView;import com.utyf.pmetro.R;import com.utyf.pmetro.map.Route;import org.w3c.dom.Text;/** * Created by Fedor on 10.04.2016. * * Adapter for displaying a route in the list of alternative routes */public class RouteListItemAdapter extends ArrayAdapter<Route> {    int resource;    Context context;    public RouteListItemAdapter(Context context, int resource, Route[] objects) {        super(context, resource, objects);        this.context = context;        this.resource = resource;    }    @Override    public View getView(int position, View convertView, ViewGroup parent) {        Route route = getItem(position);        View view;        if (convertView == null) {            LayoutInflater inflater = LayoutInflater.from(context);            view = inflater.inflate(resource, null);        }        else {            view = convertView;        }        TextView textView = (TextView)view.findViewById(R.id.textView3);        int minutes = Math.round(route.time);        String time;        if (minutes <= 60) {            time = String.format("%d", minutes);        } else {            int hours = minutes / 60;            minutes %= 60;            time = String.format("%d:%02d", hours, minutes);        }        String text = String.format("Time: %s, transfers: %d", time, route.numTransfers);        textView.setText(text);        return view;    }}